function y = mmedia(x)
  y = zeros(1,1,size(x,3));
  for i = 1:size(x,3)
    y(i) = mean(x(:,:,i)'(:));
  end
end
function y = mvariancia(x)
  y = zeros(1,1,size(x,3));
  for i = 1:size(x,3)
    y(i) = var(x(:,:,i)'(:),1);
  end
end
function y = mmax(x)
  y = zeros(1,1,size(x,3));
  for i = 1:size(x,3)
    y(i) = max(x(:,:,i)'(:));
  end
end
function y = mmin(x)
  y = zeros(1,1,size(x,3));
  for i = 1:size(x,3)
    y(i) = min(x(:,:,i)'(:));
  end
end
function y = msoma(x)
  y = zeros(1,1,size(x,3));
  for i = 1:size(x,3)
    y(i) = sum(x(:,:,i)'(:));
  end
end
mse_ = [];
